package Java_Fundamentals;

public class FlowControl_4 {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		char ch1 = 's';
		char ch2 = 'm';
		
		if(ch1 < ch2)
			System.out.println(ch1 + "," + ch2);
		else
			System.out.println(ch2 + "," + ch1);

	}

}
